/* default visitor */
struct default_visitor_t {
  virtual ~default_visitor_t() = default;
  virtual void operator()(const ast_token_t *) const = 0;
  virtual void operator()(const nothing_as_epsilon_t *) const = 0;
  virtual void operator()(const nothing_branch_t *) const = 0;
  virtual void operator()(const omega_branch_t *) const = 0;
  virtual void operator()(const omega_from_pets_t *) const = 0;
  virtual void operator()(const pet_branch_t *) const = 0;
  virtual void operator()(const pet_from_c_a_n_a_r_y_t *) const = 0;
