class token_t {

public:

  enum kind_t {
    a,
    c,
    d,
    g,
    n,
    o,
    r,
    t,
    y
  };   // kind_t

  kind_t get_kind() {
    return kind;
  }

  std::string get_name() {
    switch(kind) {
      case a: return "a";
      case c: return "c";
      case d: return "d";
      case g: return "g";
      case n: return "n";
      case o: return "o";
      case r: return "r";
      case t: return "t";
      case y: return "y";
    }
    return "unknown";
  }

  static std::shared_ptr<token_t> make(kind_t kind) {
    auto ptr = new token_t(kind);
    std::shared_ptr<token_t> result(ptr);
    return result;
  }

protected:

  /* Cache the kind. */
  token_t(kind_t kind): kind(kind) {}

  kind_t kind;

};   // token_t

